import React, {
  useState,
  useEffect,
  useCallback,
  useContext,
} from "react";
import {
  Box,
  Paper,
  Typography,
  Button,
  styled,
  Container,
  Avatar,
  Grid,
  Collapse,
  CardContent,
  CircularProgress,
  Card,
} from "@mui/material";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import VisibilityIcon from "@mui/icons-material/Visibility";
import { Link } from "react-router-dom";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";

const GradientPaper = styled(Paper)(({ theme }) => ({
  position: "absolute",
  height: "0px",
  width: "230px",
  display: "flex",
  left: 0,
  right: 0,
  top: 0,
  bottom: 0,
  height: "100%",
  zIndex: 1,
  backgroundColor: "transparent",
  color: "white",
  backgroundImage: "linear-gradient(180deg,transparent,rgba(6,7,13,.5))",
  alignItems: "flex-start",
  justifyContent: "flex-end",
  flexDirection: "column",
  opacity: "0",
  "&:hover": {
    opacity: "1",
  },
}));

const LinkStyle = {
  textDecoration: "none",
  color: "white",
};

const MainCard = ({ todo }) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [itemss, setItems] = useState([]);
  const [profile, setProfile] = useState([]);
  const [progress, setProgress] = useState(0);
  const [activity, setActivity] = useState();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setProgress((prevProgress) =>
        prevProgress >= 100 ? 0 : prevProgress + 15
      );
    }, 800);

    return () => {
      clearInterval(timer);
    };
  }, []);

  useEffect(() => {
    fetch(`/api/image/get_image/${todo.id}`)
      .then((res) => res.json())
      .then(
        (result) => {
          setItems(result);
        },
        (error) => {
          console.log(error);
        }
      );
  }, [todo.id]);

  useEffect(() => {
    fetch(`/api/auth/get_user`)
      .then((res) => res.json())
      .then(
        (result) => {
          setTimeout(() => {
            setProfile(result);
            setIsLoaded(true);
          }, 2000);
        },
        (error) => {
          console.log(error);
        }
      );
  }, []);

  const { userId } = useContext(AuthContext);

  const getActivity = useCallback(async () => {
    try {
      await axios
        .get(`/api/activity/get_activity/${userId}`, {
          headers: {
            "Content-Type": "application/json",
          },
        })
        .then((response) => setActivity(response.data));
    } catch (e) {
      console.log(e);
    }
  });

  const [form, setForm] = useState({
    user_id: userId,
    post_id: todo.id,
    like: 0,
    watch: 0,
  });

  const createActivity = async () => {
    if (userId === null) {
      return;
    } else
      try {
        createNotify();
        axios.put(
          "/api/activity/update_activity",
          { ...form },
          {
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
      } catch (e) {
        console.log(e);
      }
  };

  const createWatch = async () => {
    if (userId === null) {
      return;
    } else
      try {
        axios.post(
          "/api/activity/create_activity",
          { ...form },
          {
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
      } catch (e) {
        console.log(e);
      }
  };

  useEffect(() => {
    getActivity();
  }, [activity]);

  const lkpost = (post) => {

  }

  const checkLike = (post) => {
    setTimeout(() => {
      if (activity?.find((i) => i.post_id === post).like === 1) {
        form.like = -1;
        createActivity();
      } else {
        form.like = 1;
        createActivity();
      }
    }, 20000);
  };

  const checkWatch = async (post) => {
    var executed = false;
    let pst = activity?.find((i) => i.post_id === post);

    if (!executed) {
      executed = true;
      if (pst === undefined) {
        form.watch = 1;
        await createWatch();
      } else {
      }
    }
  };

  const [not, SetNot] = useState({
    user_from_id: userId,
    user_to_id: "",
    isActivity: "1"
  });

  const createNotify = async () => {

    not.user_to_id = profile.find((item) => item.id === todo.user_id).id;

    try {
      axios.post("/api/notify/create_notify", {...not}, {
        headers: {
          "Content-Type": "application/json",
        },
      });
    } catch (error) {
      console.log(error);
    }
  };

  return !isLoaded ? (
    <Grid mt={6}>
      <Container sx={{ maxWidth: "300px", height: "auto" }}>
        <Card sx={{ position: "relative" }}>
          <Box
            a
            sx={{
              objectFit: "cover",
              height: "300px",
              display: "block",
              maxWidth: 300,
              width: "228px",
              overflow: "hidden",
            }}
          >
            <CircularProgress
              sx={{ ml: 12, mt: 15 }}
              variant="determinate"
              value={progress}
            />
          </Box>
        </Card>
      </Container>
    </Grid>
  ) : (
    <Grid mt={6}>
      <Container sx={{ maxWidth: "300px", height: "auto" }}>
        <Card onMouseEnter={() => checkWatch(todo.id)} sx={{ position: "relative" }}>
          {itemss.slice(0, 1).map((items) => (
            <Box key={items.id}>
              <Box
                component="img"
                a
                sx={{
                  objectFit: "cover",
                  height: "300px",
                  display: "block",
                  maxWidth: 300,
                  width: "228px",
                  overflow: "hidden",
                }}
                src={`${items.ref}?fit=cover&auto=format`}
                alt={items.label}
              ></Box>
              {/* </Link> */}

              <GradientPaper

              >
                <Grid container sx={{ justifyContent: "space-between", mb: '25px' }}>
                  <Grid
                    sx={{
                      ml: "auto",
                      height: "28px",
                      mr: "10px",
                      display: "flex",
                    }}
                  >
                    <Typography
                      sx={{
                        display: "flex",
                        fontWeight: 400,
                        fontSize: "14px",
                        alignItems: "center",
                      }}
                    >
                      {todo.count_watch}

                      <VisibilityIcon
                        sx={{
                          ml: 1,
                        }}
                      />
                    </Typography>
                  </Grid>
                  <Grid
                    sx={{
                      height: "30px",
                      alignItems: "flex-start",
                      display: "flex",
                      alignItems: "center",
                    }}
                  >
                    <Typography
                      noWrap
                      sx={{
                        fontWeight: 500,
                        display: "flex",
                        overflowX: "hidden",
                        width: "180px",
                        height: "30px",
                      }}
                    >
                      <Typography
                        onClick={() => checkWatch(todo.id)}
                        sx={{
                          color: "white",
                          fontWeight: 400,
                          fontSize: "15px",
                          ml: "10px",
                          overflow: "hidden",
                          textOverflow: "ellipsis",
                        }}
                      >
                        {userId === null ? (
                          todo.name
                        ) : (
                          <Link to={`/post/${todo.id}`} style={LinkStyle}>
